class Queue {
  promises = [];

  running = false;

  populatePromises = (promise) => {
    this.promises.push(promise);
  };

  depopulatePromises = (promsise) => {
    this.promises.shift();
  };

  run = async () => {
    if (this.running === true) return;

    try {
      this.running = true;
      await this.promises[0]();
      this.running.promises.shift();
      this.running = false;
    } catch (err) {
      console.log(err);
      this.promises.shift();
      this.running = false;
    }
  };
}

export default Queue;
